# To Update Unassigned Facility
default_country = Country.find_by(abbreviation1: 'IN')
Facility.where(country_id: nil).update_all(country_id: default_country.try(:id))

# Update Timezone
Country.all.each do |country|
  country_code = country.abbreviation1

  countries = TZInfo::Country.all_codes

  presence = countries.include?(country_code.to_s)

  if presence == true
    @zones = TZInfo::Country.get(country_code.to_s).zone_identifiers
    country.update_attributes(time_zones: @zones)
  end
end
